$(document).ready((function() {
    $("[data-bs-chart]").each((function(a, t) {
        this.chart = new Chart($(t), $(t).data("bs-chart"))
    }))
})),
function(a) {
    "use strict";
    // var t = window.location.href;
    var t = window.location.origin + window.location.pathname;
    a("#layoutSidenav_nav .sb-sidenav a.nav-link").each((function() {
        if (this.href.toLowerCase() === t.toLowerCase()) {
            a(this).addClass("active")
        }
    })), 
    a("#sidebarToggle").on("click", (function(t) {
        t.preventDefault(), a("body").toggleClass("sb-sidenav-toggled")
    }))
}(jQuery);
$('#settId, #settIddropdown').on('click', function() {
    $('#settPanel').toggleClass('show');
})
$('.topPanel .close').on('click', function() {
    $('#settPanel').removeClass('show');
})
$('.themeM button').on('click', function() {
    $('.themeM button').removeClass('apply');
    $(this).addClass('apply');
    $('body').removeClass('default');
    $('body').removeClass('light');
    $('body').removeClass('dark');
    const tema = $(this).attr('id');
    $('body').addClass( tema );
    $.ajax({ url: 'uptema/' + tema, type: 'get', dataType: 'ajax'});
})